const supportedLanguages=["en","de","es","fr","ja","pt","ru"];function getLanguageFromURL(){return new URLSearchParams(window.location.search).get("lang")}function getSystemLanguage(){return(navigator.language||navigator.userLanguage).split("-")[0]}function loadLanguage(e){fetch(`lang/${e}.json`).then((e=>e.json())).then((e=>{document.getElementById("title").innerText=e["Get Unlimited <br>Access"],document.getElementById("advantage-title-1").innerText=e["Unlimited Art <br>Creation"],document.getElementById("advantage-title-2").innerText=e["Exclusive <br>Styles"],document.getElementById("advantage-title-3").innerText=e["Magic Avatars <br>With 20% Off"]})).catch((()=>loadLanguage("en")))}function init(){const e=getLanguageFromURL(),n=getSystemLanguage();loadLanguage(supportedLanguages.includes(e)?e:supportedLanguages.includes(n)?n:"en")}window.onload=init;